import { useContext, useState, useEffect } from 'react'

import { Langcontext } from './context/Langcontext';
import {useParams} from 'react-router-dom'
import './Productpage.css'

import Header from './includes/Header';

import Footer from './includes/Footer';



function Productpage(props) {
    let [lang, setlang] = useState()
    let [count, setcount] = useState()
    let [globallang, setgloballang] = useState("eng")
    const params =  useParams()
    let [language, setlanguage] = useState("eng")
    let[productdata, setproductdata] = useState([])
    console.log(params)



    const langset = (message) => {
        let arr = message.split("§§")
        setlang(arr[0])
        props.lang(message)
      };


      useEffect(()=>{    
        setgloballang(props.language)
        console.log("wqeijqiwoeqwe")
        console.log(params.artid)
        console.log("wqeqweqdfq")
        fetchproductdata()
      },[])

      useEffect(()=>{    
        renderproduct()
      },[productdata])


    function fetchproductdata(){
        fetch('https://squid-app-9h43v.ondigitalocean.app/api/products?filters[artid][$eq]='+params.artid.toString())
        .then(res=>res.json())
        .then(json=>{setproductdata(json);console.log(json)}).then(console.log(productdata)).then(()=>renderproduct())
    }
    
    function renderproduct(){
        console.log("Renderproducts called")
        console.log(productdata.data[0].attributes.title)
        return(
            <h1>{productdata.data[0].attributes.title}</h1>
        )
    }

  return (
  <>
    <Header language={props.language} lang={langset}></Header>
    <div className='page'>
    <div className='page-wrap'>
        <h1>Productpage</h1>
        {renderproduct}
  </div>  
  </div>
  <Footer language={props.language}></Footer>
  </>
    
  )
}

export default Productpage
