import React from 'react'
import './Mainpagetext.css'
import {useState, useEffect } from 'react'

function Mainpagetext() {

    let [infotext, setinfotext] = useState({})
    let [infotextlang, setinfotextlang] = useState("eng")
    useEffect(() => { 
        if(localStorage.getItem("Lang")){
            setinfotextlang(localStorage.getItem("Lang"))
            console.log(infotextlang)
            console.log(localStorage.getItem("Lang"))
        }
    })


    useEffect(() => {     fetch('http://localhost:8080/text/maintext/'+infotextlang)
    .then(res=>res.json())
    .then(json=>setinfotext(json)).then(console.log(infotext))},localStorage.getItem("Lang"))
  return (
    <div className='Mainpagetext-wrap'>
        <h2>{infotext[0]}</h2>
        <br />
        <p>{infotext[1]}</p>
        <br />
       <p>{infotext[2]}</p> 

    </div>
  )
}

export default Mainpagetext
