import React, { useEffect, useState } from 'react'
import './Systemblock.css'

function Systemblock(props) {

    let [systemarr, setsystemarr] = useState([])
    let [systemarr2, setsystemarr2] = useState([])
    let arr = []

    let [page, setpage] = useState(1)

    useEffect(()=>{
        fetch('https://squid-app-9h43v.ondigitalocean.app/api/systems')
        .then(res=>res.json())
        .then(json=>setsystemarr(json))
        .then(console.log(systemarr))
        .then(()=>rerender)
       
    
    },[])



    async function rerender(){

      for (let i = 0; i < systemarr.data.length; i++) {
       
        setsystemarr2([systemarr.data[i]])
        console.log(systemarr2)
      }
    }

  return (
    <>
    <div>Systemblock</div>
    <h1>{props.lange}</h1>
    <div className='System-Wrapper'>
    <div className='System-Filter'>
      {
          systemarr2.data.map((statss, index) => (
              <li> 
                <a >{statss.attributes.title}</a>
              </li>
          
             ))
      }
    </div>
    <div className='System-cards'>
    
    </div>
     </div>
    </>
  )
}

export default Systemblock
