import React, { useEffect, useState } from 'react'
import './Footer.css'

function Footer(props) {
 

let [langstate, setlangstate] = useState();
  useEffect(()=>{


    fetch('https://squid-app-9h43v.ondigitalocean.app/api/texts?filters[systemtitel][$eq]=footer&filters[language][$eq]='+props.language)
    .then(res=>res.json())
    .then(json=>setlangstate(json)).then(console.log(langstate.data[0].attributes)).then(()=>footerrender())


    footerrender(props.language)
    setlangstate(props.language)
  },[props.language])

  function footerrender(prop){


    

    if(props.language){
      return(
        <>

        {langstate}
        </>
      )
    }else{
      return(
        <>
        </>
      )
    }
  }


  return (
    <div className='Footer-wrap'>
      <div className='Footer-inner'>
      <div className='Footer-top'>
        <div className='Footer-Links'>
          <ul>
            {footerrender()}
          </ul>
        </div>
        <div className='Footer-Address'>

        </div>
      </div>
      <div className='Footer-bot'>

      </div>
      </div>
    </div>
  )
}

export default Footer
