import React from 'react'
import './Mainpagetext.css'
import {useState, useEffect } from 'react'

function Mainpagetext(props) {

    let [infotext, setinfotext] = useState({})
    let [infotextlang, setinfotextlang] = useState("eng")

    
    useEffect(()=>{
        setinfotextlang(localStorage.getItem("Lang"))
        if(props.lang){
            setinfotextlang(props.lang)
        
            fetchdata(props.lang)
        }
        fetchdata()
    }, [props.lang])
 

    async function fetchdata(infotextlang){
        fetch('https://squid-app-9h43v.ondigitalocean.app/api/texts?filters[systemtitel][$eq]=mainpagetext&filters[language][$eq]=ger')
     
        .then(res=>res.json())
        .then(json=>setinfotext(json)).then(console.log(infotext.data[0].attributes))
    }
    
  return (
    <div className='Mainpagetext-wrap'>
        <h2>{infotext.data[0].attributes.title}</h2>
        <br />
        <p>{infotext.data[0].attributes.text1}</p>
        <br />
       <p>{infotext.data[0].attributes.text1}</p> 
      
    </div>
  )
}

export default Mainpagetext
