import React, { useEffect, useState } from 'react'
import './Footer.css'

function Footer(props) {
 

let [langstate, setlangstate] = useState();
  useEffect(()=>{
    console.log("prefetch:"+props.language)



    fetcher(props.language)

  },[props.language])

  function fetcher(ll){
    fetch('https://squid-app-9h43v.ondigitalocean.app/api/texts?filters[systemtitel][$eq]=footer&filters[language][$eq]='+ll.toLowerCase())
    .then(res=>res.json())
    .then(json=>setlangstate(json)).then(console.log(langstate))
  }

  function footerrender(prop){


    

    if(langstate){
      return(
        <>
          {langstate.data}
      
        </>
      )
    }else{
      return(
        <>
        </>
      )
    }
  }


  return (
    <div className='Footer-wrap'>
      <div className='Footer-inner'>
      <div className='Footer-top'>
        <div className='Footer-Links'>
          <ul>
            {footerrender()}
          </ul>
        </div>
        <div className='Footer-Address'>

        </div>
      </div>
      <div className='Footer-bot'>

      </div>
      </div>
    </div>
  )
}

export default Footer
