import React from 'react'
import {useState, useEffect } from 'react'
import {useParams} from 'react-router-dom'
import './Dndslider.css'

function Dndslider() {

    let initialX = null;
let initialY = null;

let [sliderarr, setsliderarr] = useState(["https://www.q-railing.com/files/thumbnails/prime-system-slider-001.1500x637x1.jpg"
,"https://www.q-railing.com/files/thumbnails/prime-system-slider-002.1500x637x1.jpg",
"https://www.q-railing.com/files/thumbnails/prime-system-slider-004.1500x637x1.jpg",
"https://www.q-railing.com/files/thumbnails/prime-system-slider-005.1500x637x1.jpg",
"https://www.q-railing.com/files/thumbnails/prime-system-slider-006.1500x637x1.jpg"
])
let [slidecount, setslidecount] = useState(0)
let [slide1, setslide1] = useState(0)
let [slide2, setslide2] = useState(0)
let [slide3, setslide3] = useState(0)

function nextslide(){
    console.log("next")
}
function prefslide(){
    console.log("pref")

    setslidecount(slidecount+1)
    console.log(document.getElementById("contentslide"+slidecount))
    if(document.getElementById("contentslider-inner-id").classList.contains("moveleft"))
    {
        document.getElementById("contentslider-inner-id").classList.remove("moveleft")
    }
    document.getElementById("contentslider-inner-id").classList.add("moveleft")
    setTimeout(()=>{document.getElementById("contentslider-inner-id").style.left = - document.querySelectorAll(".contentslides")[0].clientWidth*slidecount + "px"},500)
    
}

function startTouch(e) {
  initialX = e.touches[0].clientX;
  initialY = e.touches[0].clientY;
};
function moveTouch(e) {
  if (initialX === null) {
    return;
  }
  if (initialY === null) {
    return;
  }
  let currentX = e.touches[0].clientX;
  let currentY = e.touches[0].clientY;
  let diffX = initialX - currentX;
  let diffY = initialY - currentY;
if (Math.abs(diffX) > Math.abs(diffY)) {
    if (diffX > 0) { // swiped left
      // moveToSlide(currentSlide+1); hooking up our next slide function
    } 
else { // swiped right
       //moveToSlide(currentSlide+-1); hooking up our prev slide function
    }
}
  initialX = null;
  initialY = null;
  e.preventDefault();
};
  
  return (
    <>
     <div className='Contentslider'>
            
            <div className='Contentslider-inner' id="contentslider-inner-id">
                    <div>
                        <img />
                    </div>

            </div>
            <button onClick={()=>prefslide()}>Pref</button><button onClick={()=>nextslide()}>next</button>
     </div>
  </>
  )
}

export default Dndslider
