import React from 'react'
import {useState, useEffect } from 'react'
import {useParams} from 'react-router-dom'
import './NewsBlog.css'

function NewsBlog() {

    let para = useParams()
    let langarr
    let [sysdata, setsysdata] = useState({data:[]})

    useEffect(()=>{

        langarr = para.count.split("-")
        console.log(langarr)
        fetch('https://squid-app-9h43v.ondigitalocean.app/api/'+langarr[0]+'-news')
        .then(res=>res.json())
        .then(json=>{setsysdata(json); console.log(json)}).then(()=>renderdata())

      },[para.count])



    function renderdata(){
        if(sysdata.data.length > 0){
            return(
                <>
                    hello
                    {sysdata.data[0].titel1}
                </>
            )
        }else{
            return(
                <>
                    fetching
                </>
            )
        }
    }

  return (
    <div>




        {renderdata()}

    </div>


  )
}

export default NewsBlog
