import React from 'react'
import './Newletterinvite.css'
import {useState, useEffect } from 'react'

function Newletterinvite(props) {


    let [infotext, setinfotext] = useState({data:[]})
    let [infotextlang, setinfotextlang] = useState("ger")


    useEffect(()=>{
      fetchdata(props.language)
    },[props.language])


    async function fetchdata(ee){
    
        fetch('https://squid-app-9h43v.ondigitalocean.app/api/texts?filters[systemtitel][$eq]=newletterinvite&filters[language][$eq]='+ee.toLowerCase())
        .then(res=>res.json())
        .then(json=>setinfotext(json)).then(()=>lollol()).then(console.log(infotext))
    }

    function lollol(){
      if(infotext.data){
        return(
          <>
          <h2>{infotext.data[0].attributes.title}</h2>
        <br />
        <p>{infotext.data[0].attributes.text1}</p>
        <br />
       <p>{infotext.data[0].attributes.text2}</p> 
      
          </>
        )
      }else{
  
        return(
          <>
          <h1>fetching</h1>
          </>
        )
      }
    }
    
  return (
    <div className='Mainpagetext-wrap'>


     
    </div>
  )
  
}

export default Newletterinvite
