import React from 'react'
import {useState, useEffect } from 'react'
import {useParams} from 'react-router-dom'
import './AboutBlock.css'

function AboutBlock() {

    let para = useParams()
    let langarr
    let [sysdata, setsysdata] = useState({data:[]})
    let [Unternehmendata, setUnternehmendata] = useState({data:[]})
    let [Geschichtedata, setGeschichtedata] = useState({data:[]})
    let [Standortdata, setStandortdata] = useState({data:[]})
    let [Visiondata, setVisiondata] = useState({data:[]})
    let [Abteilungdata, setAbteilungdata] = useState({data:[]})


    useEffect(()=>{

        langarr = para.count.split("-")
        console.log(langarr)
        fetch('https://squid-app-9h43v.ondigitalocean.app/api/'+langarr[0]+'-news')
        .then(res=>res.json())
        .then(json=>{setsysdata(json); console.log(json)}).then(()=>renderdata())

      },[para.count])

    function fetchdata(id, attrib){
        console.log("ya")
    }

    function renderdata(){
        if(sysdata.data.length > 0){
            return(
                <>
                    <div className='News-Cards'>
                    {sysdata.data.map((item)=>{
                        return(
                            <>
                                <div className='News-Card'>
                                    <div className='News-Card-img'>
                                        <img src={item.attributes.image1}/>
                                    </div>

                                    <div className='News-Card-text'>
                                        <h3>{item.attributes.titel2}</h3>
                                        <br />
                                        <p>
                                            {item.attributes.text1}
                                        </p>
                                        <a className='News-Btn' href="/news/"> {item.attributes.titel3}</a>
                                    </div>

                                </div>
                            </>
                        )
                    })}
                    </div>
                </>
            )
        }
        
        
        
        
        else{
            return(
                <>
                    fetching
                </>
            )
        }
    }

  return (
    <div>


        <div className='NewsBlogHeader'><h1>Ãber Uns</h1></div>
        <div className='NewsBlog-Menus'>
        <div>
            <div className='System-Filter-Block'>
        <h3>GelÃ¤ndertyp: </h3>
        <br />
             
        <button onClick={()=>fetchdata("Unternehmen")}>Das Unternehmen</button>
        <button onClick={()=>fetchdata("Unternehmensgeschichte")}>Unternehmensgeschichte</button>
        <button onClick={()=>fetchdata("Standorte")}>Standorte</button>
       

      </div>



            </div>
            <div>
        {renderdata()}
            </div>
        </div>
    </div>


  )
}

export default AboutBlock
