import React from 'react'
import './Mainpagetext.css'
import {useState, useEffect } from 'react'

function Mainpagetext() {

    let [infotext, setinfotext] = useState({})
    let [infotextlang, setinfotextlang] = useState()
    useEffect(() => { 

            let varyy = localStorage.getItem("Lang");
            setinfotextlang(localStorage.getItem("Lang"))
            console.log("####")
            console.log(infotextlang)
            console.log(localStorage.getItem("Lang"))
            console.log("####")
            fetchdata()

        
    }, [])

    async function fetchdata(){
        fetch('http://localhost:8080/text/maintext/'+infotextlang)
     
        .then(res=>res.json())
        .then(json=>setinfotext(json)).then(console.log(infotext))
    }
    
  return (
    <div className='Mainpagetext-wrap'>
        <h2>{infotext[0]}</h2>
        <br />
        <p>{infotext[1]}</p>
        <br />
       <p>{infotext[2]}</p> 

    </div>
  )
}

export default Mainpagetext
