import React, { useEffect, useState } from 'react'
import './Systemblock.css'

function Systemblock(props) {

    let [systemarr, setsystemarr] = useState([])
    let [page, setpage] = useState(1)

    useEffect(()=>{
        console.log(props.lang)
        fetch('http://localhost:8080/systems/page'+page)
        .then(res=>res.json())
        .then(json=>setsystemarr(json))
    },[props.lang])

    function loadmore(){
        if(page > 2){
            return(
                <h1>done</h1>
            )
        }
        setpage(page+1)
        fetch('http://localhost:8080/systems/page'+page)
        .then(res=>res.json())
        .then(json=>setsystemarr(...systemarr,json))
        

    }
  return (
    <>
    <div>Systemblock</div>
    <h1>{props.lange}</h1>
    <div className='Systems-Wrapper'>
    {
    systemarr.map((statss, index) => (
      <div className="Systems-card" > 
       <a href={statss.link}>
        <img src={statss.image} width="180px"></img>
        <h3>{statss.title}</h3>
       </a>
      </div>
  
     ))}

     <button onClick={()=>loadmore()}>Load more</button>
     </div>
    </>
  )
}

export default Systemblock
